/* Bod */
body {
  font-family: 'Karla', sans-serif !important;
  background: #fff;
  padding-top: 0;
  background-position: 0px -35px;
  margin-top: 172px;

  @include breakpoint('medium') {
    margin-top: initial;
  }
}

