.navPages {
  width: 100%;
  padding-top: 65px;

  @include breakpoint('medium') {
    padding-top: initial;
  }

  @include breakpoint("large") {
    width: 20%;
  }

  button:hover,
  button:focus,
  .button:hover,
  .button:focus {
    background-color: #2d5160;

  }
}

.navPages-container {
  @include breakpoint("medium") {
    background: #ff8750;
    padding: 5px;
    z-index: 0;
  }
}

.navPages {

  .navPages button:hover,
  .navPages button:focus,
  .navPages .button:hover,
  .navPages .button:focus {
    background: #508b9e;
  }

  @include breakpoint("medium") {
    text-align: left !important;
  }
}

/*The container <div> - needed to position the dropdown content*/
.navPages {
  @include breakpoint("medium") {
    /*position: relative;*/
    display: inline-block;
    top: -5px;
    width: 252px;
  }
}

nav.navPages ul li.navPages-item-page {
  display: none;
}

.navPages-item {
  @include breakpoint("medium") {
    padding: 10px;
    float: none;
    text-align: left;
  }
}

.navPages-action {
  @include breakpoint("medium") {
    font-size: 12px;
  }
}

/*---- First Column ----*/
.navPages-list {
  background: transparent;

  @include breakpoint("medium") {
    background: #fff;
  }
}

#caret-nav {
  position: relative;

  @include breakpoint('medium') {
    position: absolute;
  }
}

#caret-nav:before {

  @include breakpoint('medium') {
    position: absolute;
    display: inline-block;
    border-top: 27px solid transparent;
    border-right: 30px solid #4f899c;
    border-bottom: 29px solid transparent;
    left: 105px;
    top: -13px;
    // content: '';
    transform: rotate(-90deg);

  }

  @include breakpoint('large') {
    left: 110.009px;
  }
}

.is-open li.navPages-item a.navPages-action {
  padding-left: 0 !important;
}

a.navPage-subMenu-action.navPages-action {
  color: #c85046 !important;
  margin-bottom: 10px;
  border-bottom: grey solid 1px;
}


// #caret-nav:before {
//     @include breakpoint("medium") {
//         left: 105px;
//     }
// }

ul.navPages-list li.navPages-item a.navPages-action {
  @include breakpoint("medium") {
    padding: 0.3rem;
  }
}

.navPages ul.navPages-list li.navPages-item {
  @include breakpoint("medium") {
    text-align: left;
  }
}

/*---- Hides Original Dropdown Arrow ---*/
// i.navPages-action-moreIcon {
//   float: right;
//   margin-top: 5px;
//   display: none;
// }

/*---- Second Column ----*/

div.navPage-subMenu {
  @include breakpoint("medium") {
    position: absolute;
    height: auto;
    z-index: 200;
    width: 200px;
    left: 0;
  }
}

.navPage-subMenu.is-open {
  @include breakpoint("medium") {
    display: inline-block;
    border: none;
  }
}

/*--- Removes second column background and border ---*/
.navPage-subMenu-item>.navPage-subMenu-action {
  @include breakpoint("medium") {
    border-bottom: 0;
    /* background-color: transparent;*/
  }
}

.navPages ul.navPages-list li.navPages-item ul.navPage-subMenu-list li {
  @include breakpoint("medium") {
    max-width: 300px;
    /*  overflow: auto;*/
    border-radius: 5px;
  }

}

.navPages ul.navPages-list li.navPages-item ul.navPage-subMenu-list li:hover {
  @include breakpoint("medium") {
    color: #ff8855;
  }
}

.navPage-subMenu.is-open {
  @include breakpoint("medium") {
    float: right !important;
    left: 230px;
    width: 75%;
    background: #fff;
    border: none;
    border-radius: 5px;
    height: auto;
    padding: 10px;
  }
}

.navPages-action.is-open {
  background: transparent;
}

a.has-subMenu:after {
  @include breakpoint("medium") {
    // content: '';
    border: 4px solid transparent;
    border-top: 4px solid #999;
    margin-left: 2px;
    margin-bottom: 3px;
    display: inline-block;
    vertical-align: bottom;
  }
}

.navPages ul.navPages-list li.navPages-item ul.navPage-subMenu-list li ul.navPage-childList {
  @include breakpoint("medium") {
    visibility: hidden;
    position: absolute;
    top: auto;
    opacity: 0;
    margin: 0;
    padding: 10px;
    margin-top: -52px;

  }
}

.navPages ul.navPages-list li.navPages-item ul.navPage-subMenu-list li:hover ul.navPage-childList {
  @include breakpoint("medium") {
    visibility: visible;
    opacity: 1;
    overflow: auto;
    max-height: 300px;
    min-height: 150px;
  }
}

.navPages ul.navPages-list li.navPages-item ul.navPage-subMenu-list li:hover ul.navPage-childList {
  @include breakpoint("medium") {
    float: left !important;
    left: 152px;
    width: 100%;
    height: auto;
    background: #e4ecef;

  }
}

/*-- Sub Menu Drop Down --*/

.navPages-action.has-subMenu {
  @include breakpoint("small") {
    padding-left: 20px !important;
  }
}

.navPage-subMenu-item {
  @include breakpoint("medium") {
    display: block;
    float: none;
    text-align: left;
  }
}

.navPage-subMenu.is-open {
  @include breakpoint("medium") {
    float: right !important;
    left: 200px;
    width: 100%;
    background: #fff;
    top: 0;
  }
}

a.navPages-action.has-subMenu.is-open {
  background: #dde0e2;
}

.navPage-subMenu-item>.navPage-subMenu-action {
  @include breakpoint("medium") {
    margin-bottom: 0;
  }
}


ul.navPage-subMenu li ul {
  @include breakpoint("medium") {
    visibility: visible;
    opacity: 1;
  }
}

ul.navPage-subMenu li:hover ul {
  @include breakpoint("medium") {
    visibility: visible;
    opacity: 1;
  }
}

ul.navPage-subMenu ul.navPage-subMenu-list li {
  @include breakpoint("medium") {
    width: 250px;
    float: none;
    display: block;
    position: relative;
  }
}

/*-- Dropdown Button --*/
.dropbtn {
  display: none;

  @include breakpoint("medium") {
    display: inline-flex;
    background-color: #fff;
    padding: 13px 0px 24px 68px;
    font-size: 18px;
    border: none;
    height: 50px;
    font-weight: 600;
    width: 100%;
    color: #515151;
    text-transform: capitalize;
    font-family: "Karla";
    font-size: 16px;
    font-weight: 600;
  }
}

ul#caret-nav2 {
  position: relative;

  @include breakpoint('medium') {
    position: absolute;
    top: 40px;
    right: 0;
  }
}

.help-button {
  display: none;

  img {
    margin-left: 10px;
  }

  @include breakpoint("medium") {
    background-color: #fff;
    border: none;
    height: 50px;
    font-weight: 600;
    color: #515151;
    text-transform: capitalize;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: nowrap;
    font-family: "Karla";
    font-size: 16px;
    font-weight: 600;
    position: absolute;
    right: 42px;
  }
}

/*-- start ---*/
.navPages-list {
  @include breakpoint("medium") {
    display: none;
    // position: fixed;
    position: relative;
    background-color: #fff;
    padding: 10px 0 20px 0 !important;
    top: 50px;

    /*border-left: 3px solid #ff8750;
 border-right: 3px solid #ff8750;*/

    min-width: 200px;
    margin-top: 10px;

    /* z-index: 1;
  width:100%;
  max-width: 1170px;
  height: 100%;*/
  }

  min-width: 220px;
}


.navPages .navPage-subMenu-list {
  background: initial;

  @include breakpoint('medium') {
    background: #dde0e2;
    padding: 10px;
  }
}

.navPages:hover .navPages-list {
  @include breakpoint("medium") {
    display: block;
    background: #fff;
  }
}

a.navPage-subMenu-action.navPages-action.has-subMenu {
  padding-left: 0 !important;
}

.navPage-subMenu-item-child .navPage-subMenu-action.is-open,
.navPage-subMenu-item-child .navPage-subMenu-action:hover,
.navPage-subMenu-item-parent .navPage-subMenu-action.is-open,
.navPage-subMenu-item-parent .navPage-subMenu-action:hover {
  opacity: 1;
}

.sub-nav ul li a {
  font-size: 13px;

  @include breakpoint('medium') {
    font-size: 16px;
  }
}

ul.navPages-list li.navPages-item a.navPages-action {
  @include breakpoint("medium") {
    max-width: 300px;
    color: #515151;
    width: 100%;

    &:hover {
      color: #c85046;
    }
  }
}

li.navPage-subMenu-item a.navPages-action {
  @include breakpoint("medium") {
    display: block !important;
  }
}


.navPage-childList-action.navPages-action {
  line-height: 17px;
}

.navPages-action {
  @include breakpoint("medium") {
    padding: 0;
  }
}

.navPage-subMenu-item-child .navPage-subMenu-action.is-open,
.navPage-subMenu-item-child .navPage-subMenu-action:hover,
.navPage-subMenu-item-parent .navPage-subMenu-action.is-open,
.navPage-subMenu-item-parent .navPage-subMenu-action:hover {
  background: none;
}

/*---  End Menu ---*/

/*--- Flexable Sub Nav ----*/

.subNav-container {
  display: none;

  @include breakpoint("medium") {
    display: block;
    padding: 0;
    margin: 0;
    list-style: none;
    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-flow: row wrap;
    justify-content: space-around;
    margin-bottom: 10px;
  }
}

.subNav-item {
  display: none;

  @include breakpoint("medium") {
    display: block;
    background: #fff;
    padding: 5px;
    width: 200px;
    height: 60px;
    margin-top: 10px;
    line-height: 30px;
    font-weight: bold;
    font-size: 13px;
    text-align: left;
  }
}

.subNav-item a {
  @include breakpoint("medium") {
    color: #e4ecef;
    text-decoration: none;
  }
}

.subNav-item a:hover {
  @include breakpoint("medium") {
    color: #666;
  }
}

/*------End Nav Styles From Cosmobio ------*/

#main-menu-wrap {
  background: rgba(255, 255, 255, 0.65);
  display: none;
  border-top: 2px solid #E1E1E1;

  @include breakpoint('medium') {
    display: block;
  }

  .pages-menu>li>a {
    color: #595959;
    padding: 0px 15px 0px 15px !important;
    font-size: 16px !important;
    text-transform: uppercase !important;
    text-decoration: none;
    font-weight: bold;
    width: 100%;
  }

  .pages-menu {
    margin: 0;
  }

  .row.flex-container {
    align-items: flex-start;
  }

  .row.flex-container .col-sm-3 {
    width: 33%;
  }

  #menu {
