/*---------------------------- Part Schimatics ---------------------------------*/
ul.pdfs li {
  display: block;
  float: none;
  width: 100%;
  margin: 10px;
  text-align: center;
  font-size: 13px;

  @include breakpoint('medium') {
    display: inline-grid;
    float: left;
    width: 30%;
  }
}


//bulk product
#bulk-order-form {
  margin: 0 auto;
  width: 90%;
}

#bulk-product {
  width: 100%;
}

.bulk-form-field {
  display: grid;
  text-align: center;
}

.bulk-form-row {
  display: grid;
  grid-template-columns: 100%;
  grid-column-gap: 8px;
  border-top: 1px solid red;
}

.bulk-form-row:nth-child(1) {
  border-top: none;
}

.bulk-form-row:nth-last-child(1) {
  border-top: none;
}

.bulk-form-col {
  padding: 15px;
  display: block;
  align-items: center;
  justify-content: center;
  border-bottom: 2px dotted #e5e5e5;
}

.bulk-form-col:nth-last-child(1) {
  border-bottom: none;
}

.price-text,
.product-text {
  font-weight: 700;
}

.bulk-form-col .mobile-wrap {
  display: grid;
  align-items: center;
  justify-content: center;
  grid-template-columns: 100%;
}

.bulk-product-image {
  max-height: 100px;
  width: auto;
  box-shadow: 1px 2px 4px rgba(0, 0, 0, 0.25);
  transition: .2s;
}

.bulk-product-image:hover {
  box-shadow: 2px 3px 6px rgba(0, 0, 0, 0.15);
}

.bulk-add-cart {
  width: 100%;
  margin: 0 auto;
  grid-column: unset;
  grid-row: 1;
}

.message {
  position: absolute;
  padding: 0;
  margin: 10px;
}

.bulk-form-col .form-increment {
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  width: 150px;
  margin: 0 auto;
}

.bulk-form-col .form-increment .form-input {
  margin: 0 20px;
  padding: 1rem;
}

.button {
  padding: 0.85714rem 1.28571rem;
}

.bulk-form-col .icon svg {
  display: inline-block;
  fill: #757575;
  height: 100%;
  vertical-align: top;
  width: 50%;
}

.mobile-wrap p {
  margin-bottom: 0;
}

.bulk-header {
  display: none;
}

#bulk-product {
  margin-bottom: 40px;
}

@media (min-width: 768px) {

  .bulk-form-col {
    border-bottom: unset;
  }

  .bulk-form-row {
    border-top: 1px solid #e5e5e5;
  }

  .bulk-add-cart {
    grid-column: 3/5;
  }

  .bulk-form-col .form-increment {
    display: flex;
    align-items: center;
    justify-content: space-evenly;
  }

  #bulk-product {
    width: 90%;
  }

  .bulk-header {
    display: block;
  }

  .bulk-add-cart {
    grid-column: 8;
  }

  .bulk-form-row {
    grid-template-columns: 5% 15% 5% 25% 10% 10% 10% 20%;
  }

  .bulk-form-col {
    padding: 15px;
    padding: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .bulk-form-col .mobile-wrap span {
    display: none;
  }
}

